import './App.css';
import {Task} from './components/Task';
import {useState} from 'react';

function App() {
  let tasks = [
    {task: "Walk Dogs", status: true},
    {task: "Eat", status: false}

  ];

  let [task, showTasks] = useState("");

  const display = (e) => {
    tasks.map((task, key) => {
      showTasks( <Task task={task.task} status={task.status}/>)
    });
  }

  return (
    <div className="App">
      <h1 className="Headline">Simple To-Do List</h1>
      {tasks.map((task, key) => {
        return(
          <Task task={task.task} status={task.status}/>
        );
      })}
      <button onClick={ () => {}}>Add task</button>
    </div>
  );
}

export default App;
