import './App.css';
import {Task} from './components/Task';
import {useState} from 'react';



function App() {
  let tasks = [
    {task: "Walk Dogs", status: true},
    {task: "Eat", status: false}

  ];

  return (
    <div className="App">
      <h1 className="Headline">Simple To-Do List</h1>
      {tasks.map((task, key) => {
        return(
          <Task task={task.task} status={task.status}/>
        );
      })}
      <input type="text" className="inputBox"></input>
      <button onClick={() => {tasks.push({task: "inputBox".value, status: false});}}>ADD TASK</button>
      {tasks.map((task, key) => {
        return(
          <Task task={task.task} status={task.status}/>
        );
      })}
    </div>
  );
}

export default App;
