import './App.css';
import {Task} from './components/Task';
import {useState} from 'react';

function App() {
  let tasksList = [
    {task: "Walk Dogs", status: true},
    {task: "Eat", status: false}

  ];

  let [tasks, showTasks] = useState();

  const display = (e) => {
    tasksList.map((task, key) => {
      showTasks(<Task task={task.task} status={task.status}/>)
    })
  }

  return (
    <div className="App">
      <h1 className="Headline">Simple To-Do List</h1>
      <h1>{tasks}</h1>
      {/* {tasks.map((task, key) => {
        return(
          <Task task={task.task} status={task.status}/>
        );
      })} */}
      <button onClick={ () => {display()}}>Add task</button>
    </div>
  );
}

export default App;
