import './App.css';
import {Task} from './components/Task';
import {useState} from 'react';



function App() {
  let tasksList = [
    {task: "Walk Dogs", status: true},
    {task: "Eat", status: false}

  ];

  let [tasks, showTasks] = useState("");
  let [input, curInput] = useState("");

  const display = () => {
    showTasks( tasksList.map((task, key) => { return(<Task task={task.task} status={task.status}/>)}))

  }


  return (
    <div className="App">
      <h1 className="Headline">Simple To-Do List</h1>
      <h1>{tasks}</h1>

      {/* {tasks.map((task, key) => {
        return(
          <Task task={task.task} status={task.status}/>
        );
      })} */}
      <input type="text" onInput={(e) => { curInput(e.target.value); }}></input>
      <button onClick={() => { display(); }}>ADD TASK</button>
    </div>
  );
}


export default App;
